if.where:

  				customer_id = #{param.customerId}


  				and product_id = #{param.productId}


  				and customer_name like '%${param.customerName}%'


  				and product_name like '%${param.productName}%'


  				and create_time >= #{param.timeBegin}


  				and create_time <= #{param.timeEnd}

				and enable = 1
